package com.java0423;

public class 증감연산자 {

	public  void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		int x = 5 ; int y =5 ;
		
		System.out.println(x++); //5 -> 6
		System.out.println(x);   // 6
		System.out.println(++x); //7
		System.out.println(--x); //6
		System.out.println(x--); //6 -> 5
		System.out.println(x);   //5
		
		
		
	}

}
